mapper.select:
select
id as id,
AD_NAME as adName,
PARENT_ID as parentId,
status as status,
creator as creator,
create_time as createTime,
updater as updater,
update_time as updateTime,
IMAGE_SIZES as imageSizes
from ad_space


        and id=#{id}


        and AD_NAME=#{adName}


        and PARENT_ID=#{parentId}


        and status=#{status}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and IMAGE_SIZES=#{imageSizes}

select count(*) from ad_space


        and id=#{id}


        and AD_NAME=#{adName}


        and PARENT_ID=#{parentId}


        and status=#{status}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and update_time=#{updateTime}


        and IMAGE_SIZES=#{imageSizes}

mapper.update:
update ad_space


        and AD_NAME=#{adName}


        and PARENT_ID=#{parentId}


        status=#{status},


        creator=#{creator},


        create_time=#{createTime},


        updater=#{updater},


        update_time=#{updateTime},


        IMAGE_SIZES=#{imageSizes}


where id = #{id}

mapper.delete:
delete from ad_space


        and id=#{id}


        and AD_NAME=#{adName}


        and PARENT_ID=#{parentId}


        and status=#{status}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and IMAGE_SIZES=#{imageSizes}
