mapper.select:
select
id as id,
title as title,
parent_id as parentId,
content as content,
order_no as orderNo,
path as path,
status as status,
img_path as imgPath,
creator as creator,
create_time as createTime,
updater as updater,
update_time as updateTime,
ICON_PATH as iconPath
from advertise


        and id=#{id}


        and title=#{title}


        and parent_id=#{parentId}


        and content=#{content}


        and order_no=#{orderNo}


        and path=#{path}


        and status=#{status}


        and img_path=#{imgPath}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and ICON_PATH=#{iconPath}

select count(*) from advertise


        and id=#{id}


        and title=#{title}


        and parent_id=#{parentId}


        and content=#{content}


        and order_no=#{orderNo}


        and path=#{path}


        and status=#{status}


        and img_path=#{imgPath}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and update_time=#{updateTime}


        and ICON_PATH=#{iconPath}

mapper.delete:
delete from advertise


        and id=#{id}


        and title=#{title}


        and parent_id=#{parentId}


        and content=#{content}


        and order_no=#{orderNo}


        and path=#{path}


        and status=#{status}


        and img_path=#{imgPath}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}
