mapper.select:
select
id as id,
info_typeid as infoTypeid,
operation_id as operationId,
flow_type as flowType,
status as status,
submitter as submitter,
audit_opinion as auditOpinion,
auditor as auditor,
to_char(submit_time,'yyyy-MM-dd HH24:mi:ss') as submitTimes,
to_char(audit_time,'yyyy-MM-dd HH24:mi:ss') as auditTimes,
FORMER_ID as formerId,
staff_id as staffId,
TABLE_NAMES as tableNames,
FILE_URL as fileUrl,
FILE_NAME as fileName
from audit_record


        and id=#{id}


        and info_typeid=#{infoTypeid}


        and operation_id=#{operationId}


        and flow_type=#{flowType}


        and status=#{status}


        and submitter=#{submitter}


        and audit_opinion=#{auditOpinion}


        and auditor=#{auditor}


        and submit_time=#{submitTime}


        and FORMER_ID=#{formerId}


        and staff_id=#{staffId}

select id as id, info_typeid as infoTypeid, operation_id as operationId, flow_type as flowType, status as status, submitter as submitter, audit_opinion as auditOpinion, auditor as auditor, to_char(submit_time,'yyyy-MM-dd HH24:mi:ss') as submitTimes, to_char(audit_time,'yyyy-MM-dd HH24:mi:ss') as auditTimes, FORMER_ID as formerId, staff_id as staffId, TABLE_NAMES as tableNames, FILE_URL as fileUrl, FILE_NAME as fileName from audit_record
and info_typeid=#{infoTypeid}
and operation_id=#{operationId}
and flow_type=#{flowType}
and status=#{status}
and submit_time >=#{beginTime} and submit_time <= #{endTime}

select count(*) from audit_record


        and id=#{id}


        and info_typeid=#{infoTypeid}


        and operation_id=#{operationId}


        and flow_type=#{flowType}


        and status=#{status}


        and submitter=#{submitter}


        and audit_opinion=#{auditOpinion}


        and auditor=#{auditor}


        and submit_time=#{submitTime}



        and audit_time=#{auditTime}

mapper.update:
update audit_record


        info_typeid=#{infoTypeid},


        operation_id=#{operationId},


        flow_type=#{flowType},


        status=#{status},


        submitter=#{submitter},


        audit_opinion=#{auditOpinion},


        auditor=#{auditor},


        submit_time=#{submitTime},


        audit_time=#{auditTime}


        FORMER_ID=#{formerId}


        staff_id=#{staffId}


where id = #{id}

mapper.delete:
delete from audit_record


        and id=#{id}


        and info_typeid=#{infoTypeid}


        and operation_id=#{operationId}


        and flow_type=#{flowType}


        and status=#{status}


        and submitter=#{submitter}


        and audit_opinion=#{auditOpinion}


        and auditor=#{auditor}


        and submit_time=#{submitTime}

mapper.select:
select id as id, info_typeid as infoTypeid, operation_id as operationId, flow_type as flowType, status as status, submitter as submitter, audit_opinion as auditOpinion, auditor as auditor, to_char(submit_time,'yyyy-MM-dd HH24:mi:ss') as submitTimes, to_char(audit_time,'yyyy-MM-dd HH24:mi:ss') as auditTimes, FORMER_ID as formerId, staff_id as staffId, TABLE_NAMES as tableNames, FILE_URL as fileUrl, FILE_NAME as fileName from audit_record
and operation_id=#{operationId}
and flow_type=#{flowType}
and status=#{status}
and to_char(submit_time,'yyyy-mm-dd 00:00:00') >= #{startDate}
and to_char(submit_time,'yyyy-mm-dd 23:59:59') <= #{endDate}
and info_typeid=#{infoTypeid}
and info_typeid=#{infoTypeid}
and info_typeid in (0,1,2,3)
