mapper.select:
select
id as id,
dept_name as deptName,
staff_name as staffName,
staff_type as staffType,
duty as duty,
post_type as postType,
work_phone as workPhone,
to_char(dimission_time,'yyyy-MM-dd HH24:mi:ss') as dimissionTimes,
staff_sort as staffSort,
creator as creator,
to_char(create_time,'yyyy-MM-dd HH24:mi:ss') as createTimes,
updater as updater,
to_char(update_time,'yyyy-MM-dd HH24:mi:ss') as updateTimes,
status as status
from bond_investment


        and id=#{id}


        and dept_name=#{deptName}


        and staff_name=#{staffName}


        and staff_type=#{staffType}


        and duty=#{duty}


        and post_type=#{postType}


        and work_phone=#{workPhone}


        and dimission_time=#{dimissionTime}


        and staff_sort=#{staffSort}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and update_time=#{updateTime}

select id as id, dept_name as deptName, staff_name as staffName, staff_type as staffType, duty as duty, post_type as postType, work_phone as workPhone, to_char(dimission_time,'yyyy-MM-dd HH24:mi:ss') as dimissionTimes, staff_sort as staffSort, creator as creator, to_char(create_time,'yyyy-MM-dd HH24:mi:ss') as createTimes, updater as updater, to_char(update_time,'yyyy-MM-dd HH24:mi:ss') as updateTimes, status as status from bond_investment
and staff_name like concat(concat('%',#{staffName}),'%')
and status=#{status}
and staff_sort=#{staffSort}

select count(*) from bond_investment


        and id=#{id}


        and dept_name=#{deptName}


        and staff_name=#{staffName}


        and staff_type=#{staffType}


        and duty=#{duty}


        and post_type=#{postType}


        and work_phone=#{workPhone}


        and dimission_time=#{dimissionTime}


        and staff_sort=#{staffSort}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and update_time=#{updateTime}


        and status=#{status}

mapper.delete:
delete from bond_investment


        and id=#{id}


        and dept_name=#{deptName}


        and staff_name=#{staffName}


        and staff_type=#{staffType}


        and duty=#{duty}


        and post_type=#{postType}


        and work_phone=#{workPhone}


        and dimission_time=#{dimissionTime}


        and staff_sort=#{staffSort}


        and creator=#{creator}


        and create_time=#{createTime}


        and updater=#{updater}


        and update_time=#{updateTime}
