mapper.select:
select ID as id, STOCK_CODE as stockCode, STOCK_NAME as stockName, RATE as rate, BOURSE_CODE as bourseCode, CREATE_DATE as createDate, UPDATE_DATE as updateDate, INFO_TYPE as infoType, CREATE_USER as createUser, UPDATE_USER as updateUser, STATE as state from RZRQ_CONVERT_RATE
and ID=#{ID}
and STOCK_CODE=#{stockCode}
and STOCK_NAME=#{stockName}
and to_char(UPDATE_DATE,'yyyy-mm-dd hh24:mi:ss') >= #{updateDate1}
and to_char(UPDATE_DATE,'yyyy-mm-dd hh24:mi:ss') <= #{updateDate2}
and STATE = '0'

select ID as id, STOCK_CODE as stockCode, STOCK_NAME as stockName, RATE as rate, BOURSE_CODE as bourseCode, CREATE_DATE as createDate, UPDATE_DATE as updateDate, INFO_TYPE as infoType, CREATE_USER as createUser, UPDATE_USER as updateUser, STATE as state from RZRQ_CONVERT_RATE
and ID=#{ID}
and STATE = '0'

mapper.update:
update RZRQ_CONVERT_RATE


        and STOCK_CODE=#{stockCode}


        and STOCK_NAME=#{stockName}


        RATE=#{rate},


        BOURSE_CODE=#{bourseCode},


        UPDATE_DATE=#{updateDate},


        INFO_TYPE=#{infoType},


        UPDATE_USER=#{updateUser},


        STATE=#{state},


where ID = #{id}
