mapper.select:
select a.ID AS ID, a.RZ_RATE AS rzRate, a.RQ_RATE AS rqRate, a.RZ_LATA_CHARGE AS rzLataCharge, a.RQ_LATA_CHARGE AS rqLataCharge, a.RZ_QUOTA_RATE AS rzQuotaRate, a.RQ_QUOTA_RATE AS rqQuotaRate, to_char(a.CREATE_DATE,'yyyy-MM-dd hh24:mi:ss') AS createDate, to_char(a.UPDATE_DATE,'yyyy-MM-dd hh24:mi:ss') AS updateDate, a.CREATE_USER AS createUser, a.UPDATE_USER AS updateUser, a.STATE AS state, a.AUDIT_ID AS auditId, to_char(a.TRIM_DATE,'yyyy-MM-dd') as trimDate, b.AUDITOR AS auditor, b.AUDIT_OPINION AS auditOpinion FROM RZRQ_INTEREST_RATE a LEFT JOIN AUDIT_RECORD b ON a.AUDIT_ID=b.ID
and a.ID=#{id}
and to_char(a.TRIM_DATE,'yyyy-mm-dd 00:00:00') >= #{startDate}
and to_char(a.TRIM_DATE,'yyyy-mm-dd 23:59:59') <= #{endDate}
and a.STATE=#{state} and a.STATE != '7'
and a.STATE != '7'
