mapper.select:
select ID as id, CLOSE_LINE as closeLine, CORDON as cordon, EXTRACT_LINE as extractLine, MIN_LINE as minLine, CREATE_DATE as createDate, UPDATE_DATE as updateDate, CREATE_USER as createUser, UPDATE_USER as updateUser, STATE as state, AUDIT_ID as auditId from RZRQ_WARRANT_RATIO
and ID=#{id}
and to_char(CREATE_DATE,'yyyy-mm-dd hh24:mi:ss') >= #{createDate1}
and to_char(CREATE_DATE,'yyyy-mm-dd hh24:mi:ss') <= #{createDate2}
and STATE=#{state} and STATE != '6'
and STATE != '6'

select ID as id, CLOSE_LINE as closeLine, CORDON as cordon, EXTRACT_LINE as extractLine, MIN_LINE as minLine, CREATE_DATE as createDate, UPDATE_DATE as updateDate, CREATE_USER as createUser, UPDATE_USER as updateUser, STATE as state, AUDIT_ID as auditId from RZRQ_WARRANT_RATIO
and ID=#{ID}
and STATE != '6'
