mapper.select:
select a.ID as id, a.CLOSE_LINE as closeLine, a.CORDON as cordon, a.EXTRACT_LINE as extractLine, a.MIN_LINE as minLine, to_char(a.CREATE_DATE,'yyyy-MM-dd hh24:mi:ss') AS createDate, to_char(a.UPDATE_DATE,'yyyy-MM-dd hh24:mi:ss') AS updateDate, a.CREATE_USER as createUser, a.UPDATE_USER as updateUser, a.STATE as state, a.AUDIT_ID as auditId, to_char(a.TRIM_DATE,'yyyy-MM-dd') as trimDate, b.AUDITOR AS auditor, b.AUDIT_OPINION AS auditOpinion FROM RZRQ_WARRANT_RATIO a LEFT JOIN AUDIT_RECORD b ON a.AUDIT_ID=b.ID
and a.ID=#{id}
and to_char(a.TRIM_DATE,'yyyy-mm-dd 00:00:00') >= #{startDate}
and to_char(a.TRIM_DATE,'yyyy-mm-dd 23:59:59') <= #{endDate}
and a.STATE=#{state} and a.STATE != '7'
and a.STATE != '7'

select ID as id, CLOSE_LINE as closeLine, CORDON as cordon, EXTRACT_LINE as extractLine, MIN_LINE as minLine, CREATE_DATE as createDate, UPDATE_DATE as updateDate, CREATE_USER as createUser, UPDATE_USER as updateUser, STATE as state, AUDIT_ID as auditId, TRIM_DATE as trimDate from RZRQ_WARRANT_RATIO
and ID=#{ID}
and STATE != '6'
